script:
	function help_change_lang(src, dst, sectitle) {
		var o_src = document.getElementById(src);
		var o_dst = document.getElementById(dst);
		if(o_src && o_dst) {
			o_src.style.display='none';
			o_dst.style.display='block';
			var ldiv=o_dst.getElementsByTagName('div');
			for (var i=0;i<ldiv.length;i++) {
			   if (ldiv[i].className=='help-section-title') {
			     if (ldiv[i].innerHTML) {
			        var sec=document.getElementById(sectitle);
			        if (sec) {
			           sec.innerHTML=ldiv[i].innerHTML;
			        }
			     }
			   }
			}
		}
		return false;
	}
	function help_change_alllang(lang) {
[BLOCK JSSECTIONS]
		help_change_alllang_check(lang, 'sec-[SECKEY]-[SECLANG]');
[ENDBLOCK JSSECTIONS]
		var desc=document.getElementById('helppage-description');
		if (desc) {
			var sp=desc.getElementsByTagName('span');
			for (var i=0;i 0) {
				sp[0].style.display='';
			}
		}
		var desc=document.getElementById('helppage-titles');
		if (desc) {
			var sp=desc.getElementsByTagName('span');
			for (var i=0;i 0) {
				sp[0].style.display='';
				document.title=sp[0].innerHTML;
			}
		}
		resize_helppage();
		return false;
	}
	function help_change_alllang_check(lang,secid) {
		var o1 = document.getElementById(secid);
		var o2 = document.getElementById(secid.substr(0, secid.length-5)+lang);
		if(o1 && o2) {
			if(secid.substr(secid.length-5, 5) == lang) {
				o1.style.display='block';
				var ldiv=o1.getElementsByTagName('div');
                        for (var i=0;i<ldiv.length;i++) {
                           if (ldiv[i].className=='help-section-title') {
                             if (ldiv[i].innerHTML) {
                                var secname=secid.substr(4,secid.length-10);
                                var sec=document.getElementById('helppage-sec-'+secname);
                                if (sec) {
                                   sec.innerHTML=ldiv[i].innerHTML;
                                }
                             }
                           }
                         }
			} else if(o2.className != 'inactive') {
				o1.style.display='none';
			}

		}
	}
	function help_print() {
		self.print();
	}
	//addEvent(window, 'load', help_print);
